package basicjavaprograms;

public class loopsprogram {

	public static void main(String[] args)
	{
		int i=20;
		if(i>30) {
					System.out.println("true");
			
				}else {
					System.out.println("false");
				}
		int j=20;
		while(j<10);
		{
			System.out.println(j);
			j++;
		}
		do while(j>10)
		{
			j++;
			System.out.println(j);
			break;
		}while(j<10);
	}
}
	
	
	
	
	
		

			
		
		
			
		
			
			
